mapper.select:
select  patent_name, case_number,apply_number,patent_schedule, apply_time, create_person, write_person
from  tb_patent   where  1=1

    AND case_number=#{caseNumber}


    AND apply_number=#{applyNumber}


    AND patent_Name=#{patentName}


    AND create_person=#{createPerson}


    AND patent_schedule=#{patentSchedule}


    AND apply_time=#{applyTime}


order by case
WHEN patent_schedule like '未审核%' then 1
WHEN patent_schedule like '未通过%' then 2
WHEN patent_schedule like '待认领%' then 3
WHEN patent_schedule like '编写中%' then 4
WHEN patent_schedule like '待提交%' then 5
WHEN patent_schedule like '已提交%' then 6
end
asc,apply_time desc
